import React, { Component } from 'react';

class EventList extends Component {
  state = {
    body: ''
  };

  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    if (this.state.body.trim()) {
      this.props.onAddPost(this.state);
      this.handleReset();
      window.location.reload();
    }
  };

  handleReset = () => {
    this.setState({
      body: ''
    });
  };

  componentDidMount() {

  }

  render() {
    return (
      <div>
      </div>
    );
  }
}

export default EventList;
