import React from 'react';
import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import ExpansionPanelActions from "@material-ui/core/ExpansionPanelActions";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Typography from '@material-ui/core/Typography';

export default ({event: { name, dates, _embedded, id }, getEvent, addEvent, isGoing }) => {
  return (
    <div onClick={() => getEvent(id)}>
      <a href="#" className="list-group-item" style={{color: "#555", padding: "10px 10px", textDecoration: "none"}}>
        <h4 className="list-group-item-heading" style={{color: "black"}}>{name}</h4>
        <p className="list-group-item-text" style={{marginBottom: "0px"}}>{dates.start.localDate}</p>
        <p className="venue">{_embedded.venues[0].name} in {_embedded.venues[0].city.name}</p>
      </a>
      <ExpansionPanel style={{marginTop: 0, border: "none"}}>
        <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
        <div className="column">
          <Typography>{isGoing ? "You are going." : ''}</Typography>
        </div>
        </ExpansionPanelSummary>

        <Divider />
        <ExpansionPanelActions>
          <Button size="small" color="primary" disabled={ isGoing } onClick={() => addEvent(id)} style={{marginRight: "auto"}}>I'm going</Button>
          <Button size="small">Interested</Button>
        </ExpansionPanelActions>
      </ExpansionPanel>
    </div>
  );
};
