import React, { Component } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button/Button';
import Divider from '@material-ui/core/Divider/Divider';
import List from '@material-ui/core/List/List';
import ListItem from '@material-ui/core/ListItem/ListItem';
import ListItemText from '@material-ui/core/ListItemText/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader/ListSubheader';
import Paper from '@material-ui/core/Paper';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { getFriends } from '../actions/authentication';
import { connect } from 'react-redux';
import axios from 'axios';

const friends = [
  {
    image: 'https://randomuser.me/api/portraits/men/3.jpg',
    primary: 'Yeoman',
    secondary: '@whatsup yeo',
  },
  {
    image: 'https://randomuser.me/api/portraits/women/3.jpg',
    primary: 'GGWP',
    secondary: '@goodgamewellplay',
  },
  {
    image: 'https://randomuser.me/api/portraits/men/4.jpg',
    primary: 'Sawasdee',
    secondary: '@helloTH',
  },
];

const styles = theme => ({

});

class Friends extends Component {
  constructor(props) {
    super(props);

    this.showFriends = this.showFriends.bind(this);
    this.addFriends = this.addFriends.bind(this);
    this.pendingFriends = this.pendingFriends.bind(this);
  }

  showFriends() {
    axios.get('/api/users/me/friends')
        .then(res => {
          // console.log(res.data);
        })
  }

  addFriends() {

  }

  pendingFriends() {

  }

  render() {
    return (
      <Paper>
        <List subheader={<ListSubheader>Friends</ListSubheader>}>
        <Button variant="outlined" color="primary" onClick={this.showFriends}>
          Show
        </Button>
        <Button variant="contained" color="primary" onClick={this.addFriends}>
          Add
        </Button>
        <Button variant="contained" color="primary" onClick={this.pendingFriends}>
          Pending
        </Button>
          {friends.map(({ image, primary, secondary }) => (
            <React.Fragment key={primary}>
              <ListItem button>
                <Avatar link src={image} />
                <ListItemText primary={primary} secondary={secondary} />
              </ListItem>
              <Divider />
            </React.Fragment>
          ))}
          <ListItem button>
            <ListItemText>
              <Typography link>Show More</Typography>
            </ListItemText>
          </ListItem>
        </List>
      </Paper>
    );
  };
}

Friends.propTypes = {
    getFriends: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
});

export default connect(mapStateToProps, { getFriends })(withStyles(styles)(Friends));
