import React, { Component, useState } from 'react';
import Input from '@material-ui/core/Input';
import { InlineDateTimePicker } from "material-ui-pickers";

const [selectedDate, handleDateChange] = useState("2018-01-01T00:00:00.000Z");

class SearchEvents extends Component {
  state = {
    keyword: '',
    city: ''
  };

  handleSubmit = e => {
    e.preventDefault();
    var query = {
      keyword: document.getElementById("keyword").value,
      city: document.getElementById("city").value,
      page: 0
    };
    this.props.searchEvents(query);
  };

  componentDidMount() {

  }

  handleDateChange = e => {

  }

  render() {
    return (
      <div>
        <form>
          <div className="form-group">
            <div className="form-control">
              <Input
                id="keyword"
                placeholder="Keyword"
                inputProps={{
                  'aria-label': 'Description',
                }}
                style={{width: "100%"}}
              />
            </div>
          </div>
          <div className="form-group">
            <div className="form-control">
              <Input
                id="city"
                placeholder="City"
                inputProps={{
                  'aria-label': 'Description',
                }}
                style={{width: "100%"}}
              />
            </div>
          </div>
          <div className="picker">
          <InlineDateTimePicker
            keyboard
            ampm={false}
            label="With keyboard"
            value={"2018-01-01T00:00:00.000Z"}
            onChange={this.handleDateChange}
            onError={console.log}
            disablePast
            mask={[
              /\d/,
              /\d/,
              /\d/,
              /\d/,
              "/",
              /\d/,
              /\d/,
              "/",
              /\d/,
              /\d/,
              " ",
              /\d/,
              /\d/,
              ":",
              /\d/,
              /\d/,
            ]}
          />
        </div>
          <div className="form-group" style={{textAlign: 'right'}}>
            <button type="submit" onClick={ this.handleSubmit } className="btn btn-primary">Search</button>
          </div>
        </form>
      </div>
    );
  }
}

export default SearchEvents;
